import React, { useState, useEffect } from "react";

import {
  addBooks,
  fetchBook,
  updateBooks,
  deleteBooks,
} from "../Utilis/handleBooks";

import "../Style/AddBook.css";
import { fetchPublishers } from "../Utilis/handlePublishers";

function AddBook({ setBook }) {
  const [Book_id, setBookid] = useState("");
  const [Title, setTitle] = useState("");
  const [Author, setAuthor] = useState("");
  const [Pname, setPname] = useState();
  const [Price, setPrice] = useState("");
  const [Available, setAvailable] = useState("");

  const [publisher, setPublishers] = useState([]);

  const [isUpdating, setIsUpdating] = useState(false);

  const handleAdd = async (e) => {
    e.preventDefault();
    const newBook = {
      Book_id: Book_id,
      Title: Title,
      Author: Author,
      Name:Pname,
      Price: Price,
      Available: Available,
    };
    setBook(newBook);
    await addBooks(newBook);
  };
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetchBook(setBooks);
  }, []);
  // fetchBook(setBooks);

  useEffect(() => {
    fetchPublishers(setPublishers);
  }, []);

  const Update = async (Book_id, Title, Author,Name, Price, Available) => {
    setBookid(Book_id);
    setTitle(Title);
    setAuthor(Author);
    setPname(Name),
    setPrice(Price);
    setAvailable(Available);
    setIsUpdating(true);
  };
  const handleUpdate = async (e) => {
    // e.preventDefault();
    const newBook = {
      Book_id: Book_id,
      Title: Title,
      Author: Author,
      Pname:ame,
      Price: Price,
      Available: Available,
    };
    console.log(Title);
    updateBooks(Book_id, newBook);
    setIsUpdating(false);
  };

  const handleDelete = async (Book_id) => {
    const confirmDelete = window.confirm(
      "Are you sure you want to delete this book?"
    );
    if (confirmDelete) {
      try {
        // console.log("HELLO");
        await deleteBooks(Book_id);
        fetchBook(setBooks);
      } catch (error) {
        console.error("Error deleting book: ", +error);
      }
    }
  };

  return (
    <div>
      <div className="addBooks">
        <h2>Add new book</h2>
        {isUpdating ? (
          <form onSubmit={handleUpdate}>
            <table>
              <tr>
                <th>
