import axios from "axios";

const baseUrl = "http://localhost:5000";

const fetchPublishers = async(setPublishers)=>{
    try{
        const response = await axios.get(`${baseUrl}/publisher`)    
        setPublishers(response.data);
    }
    catch(error)
    {
        console.log(error);
    }
}

const addPublishers=async(newPublisher)=>{
    try{
        const response = await axios.post(`${baseUrl}/publisher/save`,newPublisher)
        .then(({data})=>{
            console.log(data);
        })
        console.log(response.data);
    }
    catch(error){
         console.log(error); 
         alert(error.response.data.message);  
    }
}

const updatePublishers = async(Book_id,updatePublisher)=>{
    try{
        // console.log(bookId);
        
        const response = await axios.put(`${baseUrl}/publisher/update/${TeachId}`,updatePublishers)
        .then(({data})=>{
            console.log(data);
        })
        console.log(response.data);
    }
    catch(error){
        console.log(error);
    }
}

const deletePublishers = async(Book_id)=>{
    try{
        const response = await axios.delete(`${baseUrl}/publisher/delete/${Book_id}`)
        .then(({data})=>{
            console.log(data);
        })
        console.log(response.data);
    }
    catch(error){
        console.log(error);
    }
}

export {fetchPublishers,addPublishers,updatePublishers,deletePublishers}
