import React, { useState,useEffect } from "react";

import {fetchStudents1,fetchStudents2,fetchStudents3,fetchStudents4,addStudents,updateStudents,deleteStudents } from "../Utilis/handleBorrowers";

// import "../Style/AllBooks.css";

import "../Style/Students.css";

function Borrowed() {
  const [USN, setUsn] = useState("");
  const [Name, setName] = useState("");
  const [Year, setYear] = useState("");
  const [Email, setEmail] = useState("");
  const [Book_id, setBookid] = useState("");
  const [isUpdating, setIsUpdating] = useState(false);

  const [students1, setStudents1] = useState([]);
  const [students2, setStudents2] = useState([]);
  const [students3, setStudents3] = useState([]);
  const [students4, setStudents4] = useState([]);

  const handleAdd = async (e) => {
    e.preventDefault();
    const newStudent = {
      USN: USN,
      Name: Name,
      Year: Year,
      Email: Email,
      Book_id: Book_id,
    };
    await addStudents(newStudent);
  };

  useEffect(() => {
    fetchStudents1(setStudents1);
  }, []);
  // useEffect(()=>{
  //   fetchStudents2(setStudents2);
    
  // },[]);

  // useEffect(()=>{
  //   fetchStudents3(setStudents3);
  // },[]);

  // useEffect(()=>{
  //   fetchStudents4(setStudents4);
  // },[]);
  const handleUpdate = () => {};

  const handleDelete = () => {};

  const Update = () => {};

  return (
    <div>
      <div className="addStudent">
        <h3>Add details of the borrower's</h3>
        {isUpdating ? (
          <form onSubmit={handleUpdate}>
            <table>
              <tr>
                <th>
                  <label htmlFor="USN">USN</label>
                </th>
                <th>
                  <label htmlFor="Name">Name</label>
                </th>
                <th>
                  <label htmlFor="Year">Year</label>
                </th>
                <th>
                  <label htmlFor="Email">Email</label>
                </th>
                <th>
                  <label htmlFor="BookId">Book_id</label>
                </th>
              </tr>
              <tr>
                <td>
                  <input
                    type="text"
                    id="USN"
                    value={USN}
                    onChange={(e) => setUsn(e.target.value)}
                  />
                </td>
                <td>
                  <input
                    type="text"
                    id="Name"
                    value={Name}
                    onChange={(e) => setName(e.target.value)}
                  />
                </td>
                <td>
                  <select
                    name=""
                    id="Year"
                    onChange={(e) => setYear(e.target.id)}
                  >
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="4">4</option>
                  </select>
                </td>
                <td>
                  <input
                    type="email"
                    id="email"
                    min="0"
                    value={Email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </td>
                <td>
                  <input
                    type="number"
                    id="BookId"
                    min="0"
                    value={Book_id}
                    onChange={(e) => setBookid(e.target.value)}
                  />
                </td>
              </tr>
            </table>
            <input type="submit" value="Update" />
          </form>
        ) : (
          <form onSubmit={handleAdd}>
            <table>
              <tr>
                <th>
                  <label htmlFor="USN">USN</label>
                </th>
                <th>
                  <label htmlFor="Name">Name</label>
                </th>
                <th>
                  <label htmlFor="Year">Year</label>
                </th>
                <th>
                  <label htmlFor="Email">Email</label>
                </th>
                <th>
                  <label htmlFor="Bookid">Book_id</label>
                </th>
              </tr>
              <tr>
                <td>
                  <input
                    type="text"
                    id="USN"
                    value={USN}
                    onChange={(e) => setUsn(e.target.value)}
                  />
                </td>
                <td>
                  <input
                    type="text"
                    id="Name"
                    value={Name}
                    onChange={(e) => setName(e.target.value)}
                  />
                </td>
                <td>
                  <select
                    name=""
                    id=""
                    onChange={(e) => setYear(e.target.value)}
                  >
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="4">4</option>
                  </select>
                </td>
                <td>
                  <input
                    type="email"
                    id="email"
                    min="0"
                    value={Email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </td>
                <td>
                  <input
                    type="number"
                    id="BookId"
                    min="0"
                    value={Book_id}
                    onChange={(e) => setBookid(e.target.value)}
                  />
                </td>
              </tr>
            </table>
            <input type="submit" value="Add" />
          </form>
        )}
      </div>
    

      <div className="student-list">
        <div><h2>Students list</h2></div>
        
        
      </div>
    </div>
  );
}

export default Borrowed;
