import React, { useEffect, useState } from "react";
import { fetchBook,deleteBooks } from "../Utilis/handleBooks";

import "../Style/AllBooks.css";

function AllBooks({setBooks}) {
  const [bookMap, setBookmap] = useState([]);

  const [Book_id,setBookid] = useState("");
  const [Title,setTitle] = useState("");
  const [Author,setAuthor] = useState("");
  const [Price,setPrice] = useState("");
  const [Available,setAvailable] = useState("");
  
  useEffect(() => {
    fetchBook(setBookmap);
  }, []);
  fetchBook(setBooks);
  const handleDelete=async(Book_id)=>{
    const confirmDelete = window.confirm("Are you sure you want to delete this book?");
    if(confirmDelete)
    {
      try{
        // console.log("HELLO");
        await deleteBooks(Book_id);
        fetchBook(setBooks);
      }
      catch(error){
        console.error("Error deleting book: ",+error);
      }
    }
  }

  const handleUpdate=async(Book_id,Title,Author,Price,Available)=>{
    
    

  }


  return (
    <div>
      <h2>Available Books</h2>
      <div className="availableBooks">
        <table>
          <thead>
            <tr>
              <th>Book_id</th>
              <th>Title</th>
              <th>Author</th>
              <th>Price(in Rs)</th>
              <th>Available</th>
              <th className="extra"></th>
              <th className="extra"></th>
            </tr>
          </thead>
          <tbody>
            {bookMap.map((book, i) => (
              <tr key={i}>
                <td>{book.Book_id}</td>
                <td>{book.Title}</td>
                <td>{book.Author}</td>
                <td>{book.Price}</td>
                <td>{book.Available}</td>
                <td className="extra1">
                  <button className="update" onClick={()=>handleUpdate(book.Book_id,book.Title,book.Author,book.Price,book.Available)} >Update</button>
                </td>
                <td className="extra1">
                  <button className="delete" onClick={()=>handleDelete(book.Book_id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AllBooks;
