import React from "react";
import { useState,use } from "react";
import { addPublishers, deletePublishers, fetchPublishers, updatePublishers } from "../Utilis/handlePublishers";

function Publisher() {
  const [Name, setName] = useState("");
  const [Email, setEmail] = useState("");
  const [Book_id, setBookid] = useState("");
  const [isUpdating, setIsUpdating] = useState(false);
  const [publishers, setPublishers] = useState([]);

  const handleAdd = async (e) => {
    e.preventDefault();
    const Publisher = {
      Name: Name,
      Email: Email,
      Book_id: Book_id,
    };
    await addPublishers(Publisher);
  };

  useEffect(() => {
    fetchPublishers(setTeachers);
  }, []);

  const Update = async (Name, Email, Book_id) => {
    setName(Name);
    setEmail(Email);
    setBookid(Book_id);
    setIsUpdating(true);
  };

  const handleUpdate = async (e) => {
    const updatePublisher = {
      TeachId: TeachId,
      Name: Name,
      Department: Department,
      Email: Email,
      Book_id: Book_id,
    };
    updatePublishers(Book_ido, updatePublisher);
    setIsUpdating(false);
  };

  const handleDelete = async (Book_id) => {
    const confirmDelete = window.confirm(
      "Are you sure you want to delete this student?"
    );
    if (confirmDelete) {
      try {
        await deletePublishers(Book_id);
        fetchTeachers(setTeachers);
      } catch (error) {
        console.error("Error deleting book: ", +error);
      }
    }
  };

  return (
    <div>
      <div className="addStudent">
        <h2>Add details of the Publishers</h2>
        {isUpdating ? (
          <form onSubmit={handleUpdate}>
            <table>
              <tr>
                <th>
                  <label htmlFor="Name">Name</label>
                </th>
                <th>
                  <label htmlFor="Email">Email</label>
                </th>
                <th>
                  <label htmlFor="BookId">Book_id</label>
                </th>
              </tr>
              <tr>
                <td>
                  <input
                    type="text"
                    id="Name"
                    value={Name}
                    onChange={(e) => setName(e.target.value)}
                  />
                </td>

                <td>
                  <input
                    type="email"
                    id="email"
                    min="0"
                    value={Email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </td>
                <td>
                  <input
                    type="number"
                    id="BookId"
                    min="0"
                    value={Book_id}
                    onChange={(e) => setBookid(e.target.value)}
                  />
                </td>
              </tr>
            </table>
            <input type="submit" value="Update" />
          </form>
        ) : (
          <form onSubmit={handleAdd}>
            <table>
              <tr>
                <th>
                  <label htmlFor="Name">Name</label>
                </th>
                <th>
                  <label htmlFor="Email">Email</label>
                </th>
                <th>
                  <label htmlFor="Bookid">Book_id</label>
                </th>
              </tr>
              <tr>
                <td>
                  <input
                    type="text"
                    id="Name"
                    value={Name}
                    onChange={(e) => setName(e.target.value)}
                  />
                </td>
                <td>
                  <input
                    type="email"
                    id="email"
                    min="0"
                    value={Email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </td>
                <td>
                  <input
                    type="number"
                    id="BookId"
                    min="0"
                    value={Book_id}
                    onChange={(e) => setBookid(e.target.value)}
                  />
                </td>
              </tr>
            </table>
            <input type="submit" value="Add" />
          </form>
        )}
      </div>

      <div>
        <h2>Publishers of the books</h2>
      </div>
      <div className="student-list">
        <div className="stu-container">
          <div className="container1">
            <h3>First year</h3>
            <table>
              <thead>
                <tr>
                  <th>Sl No.</th>
                  <th>TeachId</th>
                  <th>Name</th>
                  <th>Department</th>
                  <th>Book</th>
                  <th className="extra"></th>
                  <th className="extra"></th>
                </tr>
              </thead>
              <tbody>
                {publishers.map((pub, i) => (
                  <tr key={i}>
                    <td>{i + 1}</td>
                    <td>{pub.Name}</td>
                    <td>{pub.Book_id}</td>
                    <td className="extra1">
                      <button
                        className="update"
                        onClick={() => Update(pub.Name, pub.Email, pub.Book_id)}
                      >
                        Update
                      </button>
                    </td>
                    <td className="extra1">
                      <button
                        className="delete"
                        onClick={() => handleDelete(pub.Book_id)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Publisher;
