import axios from "axios";

const baseUrl = "http://localhost:5000";

const fetchStudents1 = async(setStudents1)=>{
    try{
        const response = await axios.get(`${baseUrl}/student1`)    
        setStudents1(response.data);
    }
    catch(error)
    {
        console.log(error);
    }
}
const fetchStudents2 = async(setStudents2)=>{
    try{
        const response = await axios.get(`${baseUrl}/borrowed2`)    
        setStudents2(response.data);
    }
    catch(error)
    {
        console.log(error);
    }
}
const fetchStudents3 = async(setStudents3)=>{
    try{
        const response = await axios.get(`${baseUrl}/borrowed3`)    
        setStudents3(response.data);
    }
    catch(error)
    {
        console.log(error);
    }
}
const fetchStudents4 = async(setStudents4)=>{
    try{
        const response = await axios.get(`${baseUrl}/borrowed4`)    
        setStudents4(response.data);
    }
    catch(error)
    {
        console.log(error);
    }
}

const addStudents=async(addStudents)=>{
    try{
        await axios.post(`${baseUrl}/borrowed/save`,addStudents)
        // .then(({data})=>{
        //     console.log(data);
        // })
        // console.log(response.data);
    }
    catch(error){
         console.log(error);   
         alert(error.response.data.message);
         
    }
}

const updateStudents = async(USN,newStudent)=>{
    try{
        
        const response = await axios.put(`${baseUrl}/borrowed/update/${USN}`,newStudent)
        .then(({data})=>{
            console.log(data);
        })
        console.log(response.data);
    }
    catch(error){
        console.log(error);
    }
}

const deleteStudents = async(USN,Book_id)=>{
    try{
        const response = await axios.delete(`${baseUrl}/borrowed/delete/${USN}/${Book_id}`)
        .then(({data})=>{
            console.log(data);
        })
        console.log(response.data);
    }
    catch(error){
        console.log(error);
    }
}

export {fetchStudents1,fetchStudents2,fetchStudents3,fetchStudents4,addStudents,updateStudents,deleteStudents}
