import React, { useState } from "react";

import { addBook } from "../Utilis/handleBooks";

import "../Style/Admin.css";

function Admin() {

  // const [Book_id,setBookid] = useState("");
  // const [Title,setTitle] = useState("");
  // const [Author,setAuthor] = useState("");
  // const [Price,setPrice] = useState("");
  // const [Available,setAvailable] = useState("");

  const [book,setBook] = useState({
    Book_id
  })

  return (
    <div>
      <div className="addBooks">
        <h3>Add new book</h3>
        <form>
          <table border="1">
            <tr>
              <th><label htmlFor="BookId">Book_id</label></th>
              <th><label htmlFor="Title">Title</label></th>
              <th><label htmlFor="Author">Author</label></th>
              <th><label htmlFor="Price">Price(in Rs)</label></th>
              <th><label htmlFor="Available">Available</label></th>
            </tr>
            <tr>
              <td>
                <input type="number" id="BookId" value={Book_id} onChange={(e)=>setBookid(e.target.value)} min="0" />
              </td>
              <td>
                <input type="text" id="Title" value={Title} onChange={(e)=>setTitle(e.target.value)} />
              </td>
              <td>
                <input type="text" id="Author" value={Author} onChange={(e)=>setAuthor(e.target.value)} />
              </td>
              <td>
                <input type="number" id="Price" min="0" value={Price} onChange={(e)=>setPrice(e.target.value)} />
              </td>
              <td>
                <input type="number" id="Available" min="0" value={Available} onChange={(e)=>setAvailable(e.target.value)} />
              </td>
            </tr>
          </table>
          <input type="submit" value="Add" onClick={addBook(Book_id,Title,Author,Price,Available)} />
        </form>
      </div>
    </div>
  );
}

export default Admin;
