import axios from "axios";

const baseUrl = "http://localhost:5000";

const getTeacherId = async(Dept,setTeachId)=>{
    try{
        const response = await axios.get(`${baseUrl}/getteacher/${Dept}`)    
        setTeachId(response.data);
    }
    catch(error)
    {
        console.log(error);
    }
}

const fetchBook = async(setFetchBook)=>{
    try{
        const response = await axios.get(`${baseUrl}/getbooks`);
        setFetchBook(response.data);
    }
    catch(error)
    {
        console.log(error);
    }
}

const addTeachB=async(newTeacher)=>{
    try{
        await axios.post(`${baseUrl}/t_borrowed/save`,newTeacher)
    }
    catch(error){
         console.log(error);   
         alert(error.response.data.message);
         
    }
}

