import React, { useEffect, useState } from "react";

import { BrowserRouter, Routes, Route} from "react-router-dom";

import Login from "./Login";

import AddBook from "./AddBook";
import Students from "./Students";
import Teachers from "./Teachers";
import Publisher from "./Publisher";
import Stu_Borrowers from "./Stu_Borrowers";
import Due from "./Due";

// import AllBooks from "./AllBooks";

import Navbar from "./Navbar";

function Admin() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);


  const [book, setBook] = useState({
    Book_id: "",
    Title: "",
    Author: "",
    Price: "",
    Available: "",
  });
  useEffect(() => {

    const loggedIn = sessionStorage.getItem("isLoggedIn");
    setIsLoggedIn(loggedIn === "true");
  }, []);

  return (
    <div>
      {isLoggedIn ? (
        <BrowserRouter>
          <Navbar />
          <Routes>
            <Route exact path="/" element={<AddBook setBook={setBook} />}></Route>
            <Route path="/publisher" element={<Publisher/>}></Route>
            <Route path="/student" element={<Students />}></Route>
            <Route path="/teacher" element={<Teachers />}></Route>
            <Route path="/stu-borrowers" element={<Stu_Borrowers/>}></Route>
            <Route path="/teach-borrowers" element={<Stu_Borrowers/>}></Route>
            <Route path="/due" element={<Due />}></Route>
          </Routes>
        </BrowserRouter>
      )
      :
      (
        <Login setIsLoggedIn={setIsLoggedIn} />
      ) 
    }
    </div>
  );
}

export default Admin;
