         console.log(error); 
         alert(error.response.data.message);  
    }
}

const updateTeachers = async(TeachId,newTeacher)=>{
    try{
        // console.log(bookId);
        
        const response = await axios.put(`${baseUrl}/t_borrowed/update/${TeachId}`,newTeacher)
        .then(({data})=>{
            console.log(data);
        })
        console.log(response.data);
    }
    catch(error){
        console.log(error);
    }
}

const deleteTeachers = async(TeachId,Book_id)=>{
    try{
        const response = await axios.delete(`${baseUrl}/t_borrowed/delete/${TeachId}/${Book_id}`)
        .then(({data})=>{
            console.log(data);
        })
        console.log(response.data);
    }
    catch(error){
        console.log(error);
    }
}

