import React, { useState, useEffect } from "react";

import { addTeachers, deleteTeachers, fetchTeachers ,updateTeachers} from "../Utilis/handleTechBorrowers";

function Teachers() {
  const [TeachId, setTeachId] = useState("");
  const [Name, setName] = useState("");
  const [Department, setDepartment] = useState("");
  const [Email, setEmail] = useState("");
  const [Book_id, setBookid] = useState("");
  const [isUpdating, setIsUpdating] = useState(false);
  const [teachers, setTeachers] = useState([]);

  const handleAdd = async (e) => {
    e.preventDefault();
    const newTeacher = {
      TeachId: TeachId,
      Name: Name,
      Department: Department,
      Email: Email,
      Book_id: Book_id,
    };
    await addTeachers(newTeacher);
  };

    useEffect(() => {
      fetchTeachers(setTeachers);
    }, []);

 const Update = async(TeachId,Name,Department,Email,Book_id)=>{
    setTeachId(TeachId);
    setName(Name);
    setDepartment(Department);
    setEmail(Email);
    setBookid(Book_id);
    setIsUpdating(true);
  }
  const handleUpdate = async (e) => {
    
    const updateTeacher = {
      TeachId:TeachId,
      Name:Name,
      Department:Department,
      Email:Email,
      Book_id:Book_id
    };

   updateTeachers(TeachId,updateTeacher);
   setIsUpdating(false);
  }

  const handleDelete = async (TeachId,Book_id) => {
    const confirmDelete = window.confirm(
      "Are you sure you want to delete this student?"
    );
    if (confirmDelete) {
      try {
        await deleteTeachers(TeachId,Book_id);
        fetchTeachers(setTeachers);
      } catch (error) {
        console.error("Error deleting book: ", +error);
      }
    }
  };

  return (
    <div>
      <div className="addStudent">
        <h2>Add details of the borrower's</h2>
        {isUpdating ? (
          <form onSubmit={handleUpdate}>
            <table>
              <tr>
                <th>
                  <label htmlFor="TeachId">TeacherId</label>
                </th>
                <th>
                  <label htmlFor="Name">Name</label>
                </th>
                <th>
                  <label htmlFor="Department">Department</label>
                </th>
                <th>
                  <label htmlFor="Email">Email</label>
                </th>
                <th>
                  <label htmlFor="BookId">Book_id</label>
                </th>
              </tr>
              <tr>
                <td>
                  <input
                    type="text"
                    id="TeachId"
                    value={TeachId}
                    onChange={(e) => setTeachId(e.target.value)}
                  />
                </td>
                <td>
                  <input
                    type="text"
                    id="Name"
                    value={Name}
                    onChange={(e) => setName(e.target.value)}
                  />
                </td>
                <td>
                  <select
                    name=""
                    id="Department"
                    onChange={(e) => setDepartment(e.target.value)}
                  >
                    <option value="">Select department</option>
                    <option value="CSE">CSE</option>
                    <option value="ISE">ISE</option>
                    <option value="ECE">ECE</option>
                    <option value="CV">CV</option>
                    <option value="ME">ME</option>
                    {/* <option value="CV">4</option> */}
                  </select>
                </td>
                <td>
                  <input
                    type="email"
                    id="email"
                    // min="0"
                    value={Email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </td>
                <td>
                  <input
                    type="number"
                    id="BookId"
                    min="0"
                    value={Book_id}
                    onChange={(e) => setBookid(e.target.value)}
                  />
                </td>
              </tr>
            </table>
            <input type="submit" value="Update" />
          </form>
        ) : (
          <form onSubmit={handleAdd}>
            <table>
              <tr>
                <th>
                  <label htmlFor="TeachId">TeachId</label>
                </th>
                <th>
                  <label htmlFor="Name">Name</label>
                </th>
                <th>
                  <label htmlFor="Department">Department</label>
                </th>
                <th>
                  <label htmlFor="Email">Email</label>
                </th>
                <th>
                  <label htmlFor="Bookid">Book_id</label>
                </th>
              </tr>
              <tr>
                <td>
                  <input
                    type="text"
                    id="TeachId"
                    value={TeachId}
                    onChange={(e) => setTeachId(e.target.value)}
                  />
                </td>
                <td>
                  <input
                    type="text"
                    id="Name"
                    value={Name}
                    onChange={(e) => setName(e.target.value)}
                  />
                </td>
                <td>
                  <select
                    name=""
                    id="Department"
                    onChange={(e) => setDepartment(e.target.value)}
                  >
                    <option value="">Select department</option>
                    <option value="CSE">CSE</option>
                    <option value="ISE">ISE</option>
                    <option value="ECE">ECE</option>
                    <option value="CV">CV</option>
                    <option value="ME">ME</option>
                    {/* <option value="CV">4</option> */}
                  </select>
                </td>
                <td>
                  <input
                    type="email"
                    id="email"
                    // min="0"
                    value={Email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </td>
                <td>
                  <input
                    type="number"
                    id="BookId"
                    min="0"
                    value={Book_id}
                    onChange={(e) => setBookid(e.target.value)}
                  />
                </td>
              </tr>
            </table>
            <input type="submit" value="Add" />
          </form>
        )}
      </div>
      <div>
          <h2>Teachers list</h2>
        </div>
      <div className="student-list">
        
        <div className="stu-container">
          <div className="container1">
            <h3>First year</h3>
            <table>
              <thead>
                <tr>
                  <th>Sl No.</th>
                  <th>TeachId</th>
                  <th>Name</th>
                  <th>Department</th>
                  <th className="extra"></th>
                  <th className="extra"></th>
                </tr>
              </thead>
              <tbody>
                {teachers.map((teach, i) => (
                  <tr key={i}>
                    <td>{i + 1}</td>
                    <td>{teach.TeachId}</td>
                    <td>{teach.Name}</td>
                    <td>{teach.Department}</td>
                    <td className="extra1">
                      <button
                        className="update"
                        onClick={() =>
                          Update(
                            teach.TeachId,
                            teach.Name,
                            teach.Department,
                            teach.Email,
                            teach.Book_id
                          )
                        }
                      >
                        Update
                      </button>
                    </td>
                    <td className="extra1">
                      <button
                        className="delete"
                        onClick={() => handleDelete(teach.TeachId,teach.Book_id)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Teachers;
