import axios from "axios";

const baseUrl = "http://localhost:5000";

const getStudentUsn = async(Year,Dept,setStudentUsn)=>{
    try{
        const response = await axios.get(`${baseUrl}/getstudent/${Year}/${Dept}`)    
        setStudentUsn(response.data);
    }
    catch(error)
    {
        console.log(error);
    }
}

const addStuB=async(addStudents)=>{
    try{
        await axios.post(`${baseUrl}/borrowed/save`,addStudents)
        // .then(({data})=>{
        //     console.log(data);
        // })
        // console.log(response.data);
    }
    catch(error){
         console.log(error);   
         alert(error.response.data.message);
         
    }
}
export {getStudentUsn,addStuB};
