import React,{useState,useEffect} from 'react'

function Teach_Borrow() {
    const [TeachId, setTeachId] = useState("");
    const [Dept, setDept] = useState("");
    const [Book_id,setBook] = useState("");
    const [isUpdating, setIsUpdating] = useState(false);
  
    const [teacherId, setTeacherId] = useState([]);
  
    const [teacher1, setTeacher1] = useState([]);
    const [teacher2, setTeacher2] = useState([]);
    const [teacher3, setTeacher3] = useState([]);
    const [teacher4, setTeacher4] = useState([]);
    const [teacher5, setTeacher5] = useState([]);
    const [fetchbook, setFetchBook] = useState([]);
  
    const handleAdd = async (e) => {
      e.preventDefault();
      const newTeacher = {
        TeachId: TeachId,
        Dept: Dept,
        Book_id: Book_id,
      };
      await addB(newTeacher);
    };
  
    useEffect(() => {
      fetchBook(setFetchBook);
    }, []);

    useEffect(() => {
      fetchStudents1(setStudents1);
    }, []);
    useEffect(() => {
      fetchStudents2(setStudents2);
    }, []);
  
    useEffect(() => {
      fetchStudents3(setStudents3);
    }, []);
  
    useEffect(() => {
      fetchStudents4(setStudents4);
    }, []);
  
    useEffect(()=>{
      getTeacher();
    },[Dept])
  
    const getTeacher=async() =>{
      console.log(Dept);
      await getTeacherId(Dept,setStudentUsn)
    }
  
    const Update = async (USN,Dept,Year,Book_id) => {
      setUsn(USN);
      setDept(Dept);
      setYear(Year);
      setBook(Book_id);
      setIsUpdating(true);
    };
    const handleUpdate = async (e) => {
      // e.preventDefault();
      const updateStudent = {
        USN: USN,
        Dept: Dept,
        Year: Year,
        Book_id: Book_id
      };
      // updateStudents(USN, updateStudent);
      setIsUpdating(false);
    };
  
    const handleDelete = async (USN,Book_id) => {
      const confirmDelete = window.confirm(
        "Are you sure you want to delete this student?"
      );
      if (confirmDelete) {
        try {
          console.log(USN);
          await deleteStudents(USN,Book_id);
            fetchStudents1(setStudents1);
            fetchStudents2(setStudents2);
            fetchStudents3(setStudents3);
            fetchStudents4(setStudents4);
        } catch (error) {
          console.error("Error deleting book: ", +error);
        }
      }
    };
  
    return (
      <div>
        <div className="addStudent">
          <h2>Add details of the borrower's</h2>
          {isUpdating ? (
            <form onSubmit={handleUpdate}>
              <table>
                <tr>
                  <th>
                    <label htmlFor="Dept">Departmet</label>
                  </th>
                  <th>
                    <label htmlFor="USN">USN</label>
                  </th>
                  <th>
                    <label htmlFor="Email">Book</label>
                  </th>
                </tr>
                <tr>
                  <td>
                    <select
                      name=""
                      id="Year"
                      onChange={(e) => setYear(e.target.value)}
                    >
                      <option value="" hidden>
                        Choose a year
                      </option>
                      <option value="1">1</option>
                      <option value="2">2</option>
                      <option value="3">3</option>
                      <option value="4">4</option>
                    </select>
                  </td>
                  <td>
                    <select
                      name=""
                      id="Dept"
                      onChange={(e) => {
                          setDept(e.target.value);
                          getTeacher();
                      }}
                      required
                    >
                      <option value="" hidden>
                        Select department
                      </option>
                      <option value="CSE">CSE</option>
                      <option value="ISE">ISE</option>
                      <option value="ECE">ECE</option>
                      <option value="CV">CV</option>
                      <option value="ME">ME</option>
                      {/* <option value="CV">4</option> */}
                    </select>
                  </td>
                  <td>
                  <select
                  value={Book_id}
                  onChange={(e) => setBook(e.target.value)}
                  >
                      <option value="">Select Book</option>
                      {fetchbook.map((book, i) => (
                        <option key={i} value={book.Book_id}>
                          {book.Title}
                        </option>
                      ))}
                    </select>
                  </td>
                </tr>
              </table>
              <input type="submit" value="Update" />
            </form>
          ) : (
            <form onSubmit={handleAdd}>
              <table>
                <tr>
                  <th>
                    <label htmlFor="Dept">Departmet</label>
                  </th>
                  <th>
                    <label htmlFor="USN">USN</label>
                  </th>
                  <th>
                    <label htmlFor="Email">Book</label>
                  </th>
                </tr>
                <tr>
                  <td>
                    <select
                      name=""
                      id="Dept"
                      onChange={(e) => {
                          setDept(e.target.value);
                          getTeacher();
                      }}
                      required
                    >
                      <option value="" hidden>
                        Select department
                      </option>
                      <option value="CSE">CSE</option>
                      <option value="ISE">ISE</option>
                      <option value="ECE">ECE</option>
                      <option value="CV">CV</option>
                      <option value="ME">ME</option>
                      {/* <option value="CV">4</option> */}
                    </select>
                  </td>
                  <td>
                  <select
                  value={TeachId}
                  onChange={(e) => setTeachId(e.target.value)}
                  >
                      <option value="">Select Teacher Id</option>
                      {teacherId.map((teach, i) => (
                        <option key={i} value={teach.Id}>
                          {teach.USN}
                        </option>
                      ))}
                    </select>
                  </td>
                  <td>
                  <select
                  value={Book_id}
                  onChange={(e) => setBook(e.target.value)}
                  >
                      <option value="">Select Book</option>
                      {fetchbook.map((book, i) => (
                        <option key={i} value={book.Book_id}>
                          {book.Title}
                        </option>
                      ))}
                    </select>
                  </td>
                </tr>
              </table>
              <input type="submit" value="Add" />
            </form>
          )}
        </div>
        <div>
          <h2>Students list</h2>
        </div>
        <div className="student-list">
          <div className="stu-container">
            <div className="container1">
              <h3>First year</h3>
              <table>
                <thead>
                  <tr>
                    <th>Sl No.</th>
                    <th>USN</th>
                    <th>Dept</th>
                    <th>Book</th>
                    <th className="extra"></th>
                    <th className="extra"></th>
                  </tr>
                </thead>
                <tbody>
                  {teacher1.map((student, i) => (
                    <tr key={i}>
                      <td>{i + 1}</td>
                      <td>{student.USN}</td>
                      <td>{student.Dept}</td>
                      <td>{student.Title}</td>
                      <td className="extra1">
                        <button
                          className="update"
                          onClick={() =>
                            Update(
                              student.USN,
                              student.Dept,
                              student.Year,
                              student.Book_id
                            )
                          }
                        >
                          Update
                        </button>
                      </td>
                      <td className="extra1">
                        <button
                          className="delete"
                          onClick={() => handleDelete(student.USN,Book_id)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            <div className="container2">
              <h3>Second year</h3>
              <table>
                <thead>
                  <tr>
                    <th>Sl No.</th>
                    <th>USN</th>
                    <th>Book</th>
                    <th className="extra"></th>
                    <th className="extra"></th>
                  </tr>
                </thead>
                <tbody>
                  {teacher2.map((student, i) => (
                    <tr key={i}>
                      <td>{i + 1}</td>
                      <td>{student.USN}</td>
                      <td>{student.Dept}</td>
                      <td>{student.Title}</td>
                      <td className="extra1">
                        <button
                          className="update"
                          onClick={() =>
                            Update(
                              student.USN,
                              student.Dept,
                              student.Year,
                              student.Book_id
                            )
                          }
                        >
                          Update
                        </button>
                      </td>
                      <td className="extra1">
                        <button
                          className="delete"
                          onClick={() => handleDelete(student.USN,student.Book_id)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            <div className="container3">
              <h3>Third year</h3>
              <table>
                <thead>
                  <tr>
                    <th>Sl No.</th>
                    <th>USN</th>
                    <th>Book</th>
                    <th className="extra"></th>
                    <th className="extra"></th>
                  </tr>
                </thead>
                <tbody>
                  {teacher3.map((teacher, i) => (
                    <tr key={i}>
                      <td>{i + 1}</td>
                      <td>{teacher.TeachId}</td>
                      <td>{teacher.Title}</td>
                      <td className="extra1">
                        <button
                          className="update"
                          onClick={() =>
                            Update(
                              teacher.TeachId,
                              teacher.Dept,
                              teacher.Book_id
                            )
                          }
                        >
                          Update
                        </button>
                      </td>
                      <td className="extra1">
                        <button
                          className="delete"
                          onClick={() => handleDelete(teacher.TeachId,teacher.Book_id)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            <div className="container4">
              <h3>Fourth year</h3>
              <table>
                <thead>
                  <tr>
                    <th>Sl No.</th>
                    <th>USN</th>
                    <th>Book</th>
                    <th className="extra"></th>
                    <th className="extra"></th>
                  </tr>
                </thead>
                <tbody>
                  {teacher4.map((teacher, i) => (
                    <tr key={i}>
                      <td>{i + 1}</td>
                      <td>{teacher.TeachId}</td>
                      <td>{teacher.Title}</td>
                      <td className="extra1">
                        <button
                          className="update"
                          onClick={() =>
                            Update(
                              teacher.TeachId,
                              teacher.Dept,
                              teacher.Book_id
                            )
                          }
                        >
                          Update
                        </button>
                      </td>
                      <td className="extra1">
                        <button
                          className="delete"
                          onClick={() => handleDelete(teacher.TeachId,teacher.Book_id)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            <div className="container4">
              <h3>Fourth year</h3>
              <table>
                <thead>
                  <tr>
                    <th>Sl No.</th>
                    <th>USN</th>
                    <th>Book</th>
                    <th className="extra"></th>
                    <th className="extra"></th>
                  </tr>
                </thead>
                <tbody>
                  {teacher5.map((teacher, i) => (
                    <tr key={i}>
                      <td>{i + 1}</td>
                      <td>{teacher.TeachId}</td>
                      <td>{teacher.Title}</td>
                      <td className="extra1">
                        <button
                          className="update"
                          onClick={() =>
                            Update(
                              teacher.TeachId,
                              teacher.Dept,
                              teacher.Book_id
                            )
                          }
                        >
                          Update
                        </button>
                      </td>
                      <td className="extra1">
                        <button
                          className="delete"
                          onClick={() => handleDelete(teacher.TeachId,teacher.Book_id)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

          </div>
        </div>
      </div>
    );
}

export default Teach_Borrow
