import React, { useEffect, useState } from "react";

import { BrowserRouter, Routes, Route, Link } from "react-router-dom";

import axios from "axios";

import Login from "./Login";
import Change from "./Change";

import Loading from "./Loading";

import AddBook from "./AddBook";
import Students from "./Students";
import Teachers from "./Teachers";
import Publisher from "./Publisher";
import StuBorrowers from "./Stu_Borrowers";
import TeachBorrow from "./Teach_Borrow";
import Due from "./Due";


// import AllBooks from "./AllBooks";

import LibraryManagement from "./LibraryManagement";

import Navbar from "./Navbar";

function Admin() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const [loading,setLoading] = useState(false);

  useEffect(()=>{
    //request interceptor
    axios.interceptors.request.use((config)=>{
      setLoading(true);
      return config;
    },
    (error)=>{
      return Promise.reject(error);
    });

    //response interceptor
    axios.interceptors.response.use((config)=>{
      setLoading(false);
      return config;
    },
    (error)=>{
      return Promise.reject(error);
    });

  },[])

  const [book, setBook] = useState({
    Book_id: "",
    Title: "",
    Author: "",
    Price: "",
    Available: "",
  });
  useEffect(() => {
    const loggedIn = sessionStorage.getItem("isLoggedIn");
    setIsLoggedIn(loggedIn === "true");
  }, []);




  return (
    <div>
      {isLoggedIn ? (
        <BrowserRouter>

          <Loading show={loading}></Loading>

          <div className="navbar">
      <input type="checkbox" id="check" />
      <label for="check">
        <i className="fas fa-bars" id="btn"></i>
        <i className="fas fa-times" id="cancel"></i>
      </label>
      <div className="sidebar">
        <header>Menu</header>

        <Link to="/">
          <span>Student-Borrow</span>
        </Link>
        <Link to="/teach-borrowers">
          <span>Teacher-Borrow</span>
        </Link>

        <Link to="/books">
          <span>Books</span>
        </Link>
        <Link to="/publisher">
          <span>Publishers</span>
        </Link>
        <Link to="/student">
          <span>Student</span>
        </Link>
        <Link to="/teacher">
          <span>Teachers</span>
        </Link>

        <Link to="/due">
          <span>Over due</span>
        </Link>
        <Link to="/">
          <span
            onClick={() =>
              setIsLoggedIn(sessionStorage.setItem("isLoggedIn", false))
            }
          >
            Logout
          </span>
        </Link>
      </div>
    </div>
    <div>
          <LibraryManagement/>
          <Routes>
            <Route
              exact
              path="/"
              element={ <StuBorrowers/>}
            ></Route>
            <Route path="/publisher" element={<Publisher />}></Route>
            <Route path="/student" element={<Students />}></Route>
            <Route path="/teacher" element={<Teachers />}></Route>
            <Route path="/books" element={<AddBook setBook={setBook}/>}></Route>
            <Route path="/teach-borrowers" element={<TeachBorrow />}></Route>
            <Route path="/due" element={<Due />}></Route>
            <Route path="/logout"></Route>
          </Routes>
      </div>
        </BrowserRouter>
      
      ) : (
        <BrowserRouter>

          <Routes>
            <Route path="/" element={<Login setIsLoggedIn={setIsLoggedIn} />}></Route>
            <Route path="/register" element={<Change/>}></Route>
          </Routes>

        </BrowserRouter>
      )}
    </div>
  );
}

export default Admin;
