import axios from "axios";

const baseUrl = "http://localhost:5000";

const fetchBook = async(setBooks)=>{
    try{
        const response = await axios.get(`${baseUrl}/admin`)    
        setBooks(response.data);
    }
    catch(error)
    {
        console.log(error);
    }
}

const addBooks=async(book)=>{
    try{
        const response = await axios.post(`${baseUrl}/boo/save`,book)
        .then(({data})=>{
            console.log(data);
        })
        console.log(response.data);
    }
    catch(error){
         console.log(error);   
    }
}

const updateBooks = async(bookId,newBook)=>{
    try{
        console.log(bookId);
        
        const response = await axios.put(`${baseUrl}/admin/update/${bookId}`,newBook)
        .then(({data})=>{
            console.log(data);
        })
        console.log(response.data);
    }
    catch(error){
        console.log(error);
    }
}

const deleteBooks = async(bookId)=>{
    try{
        const response = await axios.delete(`${baseUrl}/admin/delete/${bookId}`)
        .then(({data})=>{
            console.log(data);
        })
        console.log(response.data);
    }
    catch(error){
        console.log(error);
    }
}

export {fetchBook,addBooks,updateBooks,deleteBooks}
