import React, { useEffect, useState } from 'react'
import { fetchBook } from '../Utilis/handleBooks';

function AllBooks() {

  const [books,setBooks] = useState([]);

  useEffect(()=>{
    fetchBook(setBooks);
  },[]);  
  return (
    <div>
        <table>
        <thead>
          <tr>
            <th>Book_id</th>
            <th>Title</th>
            <th>Author</th>
            <th>Price(in Rs)</th>
            <th>Available</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {
            books.map((book,i)=>(
              <tr key={i}>
                <td>{book.Book_id}</td>
                <td>{book.Title}</td>
                <td>{book.Author}</td>
                <td>{book.Price}</td>
                <td>{book.Available}</td>
              </tr>
            ))
          }
        </tbody>
      </table>
    </div>
  )
}

export default AllBooks
