import React, { useEffect, useState } from "react";
import { fetchBook,deleteBooks } from "../Utilis/handleBooks";

import "../Style/AllBooks.css";

function AllBooks() {
  const [books, setBooks] = useState([]);
  fetchBook(setBooks);
  useEffect(() => {
    fetchBook(setBooks);
  }, []);

  return (
    <div>
      <h2>Available Books</h2>
      <div className="availableBooks">
        <table>
          <thead>
            <tr>
              <th>Book_id</th>
              <th>Title</th>
              <th>Author</th>
              <th>Price(in Rs)</th>
              <th>Available</th>
              <th className="extra"></th>
              <th className="extra"></th>
            </tr>
          </thead>
          <tbody>
            {books.map((book, i) => (
              <tr key={i}>
                <td>{book.Book_id}</td>
                <td>{book.Title}</td>
                <td>{book.Author}</td>
                <td>{book.Price}</td>
                <td>{book.Available}</td>
                <td className="extra1">
                  <button className="update">Update</button>
                </td>
                <td className="extra1">
                  <button className="delete" onClick={deleteBooks(book)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AllBooks;
